import styled from 'styled-components';

export const Container = styled.div`
  display:flex;
  flex-direction:column;
  height:100%;
  width:100%;
  margin:18px;

`;

export const ImagemOportu  = styled.img`
    width: min(330px , 100%);
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    margin:0;
    padding:0;

`;

export const OportunidadeDoDia  = styled.span`
 display:flex;


`;

export const Marca  = styled.div``;

export const Caracteristicas  = styled.div``;

export const ValorDoCarro  = styled.div``;

export const Moeda  = styled.div``;
