import styled from 'styled-components';

export const Container = styled.div`
  display:flex;
  flex-direction:column;
  height:100%;
  width:100%;
  margin:18px;

`;

export const ImagemOportu  = styled.img`
    width: min(width: 320px; , 100%);

    margin:0;
    padding:0;
    height: 350px;

    display: flex;
    margin: 0;
    border-radius: 4px;

`;

export const OportunidadeDoDia  = styled.span`
 display:flex;


`;

export const Marca  = styled.div``;

export const Caracteristicas  = styled.div``;

export const ValorDoCarro  = styled.div``;

