import styled from 'styled-components';

export const Container = styled.div`
  display:flex;
  flex-direction:column;
  height:100%;
  width:100%;
  margin:18px;
  max-width:200px;

`;

export const ImagemOportu  = styled.img`
   vertical-align: baseline;

    margin:0;
    padding:0;
    height: 350px;
    min-width: 320px;
    width:100%;
    display: flex;

    border-radius: 4px;

`;

export const OportunidadeDoDia  = styled.h2`
    display:flex;
    position: absolute;
    padding: 8px 16px;
    border-radius: 4px;
    background:var ;
    color:var(--color-white);
    font-size: 1.375rem;
    font-weight: 600;

`;

export const Marca  = styled.div``;

export const Caracteristicas  = styled.div``;

export const ValorDoCarro  = styled.div``;

export const Moeda  = styled.div``;
