import styled from 'styled-components';

export const Container = styled.div`
  display:flex;
  flex-direction:column;
  height:100%;
  width:100%;
  margin:18px;
  max-width:200px;


`;

export const ImagemOportu  = styled.img`
   vertical-align: baseline;

    margin:0;
    padding:0;
    height: 350px;
    min-width: 320px;
    width:100%;
    display: flex;

    border-radius: 4px;
    margin-bottom:10px;

`;

export const OportunidadeDoDia  = styled.h2`
    display:flex;

    padding: 8px 16px;
    border-radius: 4px;
    background:var(--color-header) ;
    color:var(--color-white);
    font-size: 1.375rem;
    font-weight: 600;
    position: absolute;


`;

 export const Marca  = styled.div`
  display:flex;
  margin-bottom: 4px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  font-size: 16px;
  line-height: 20px;
  font-weight: 600;
  letter-spacing: normal;
  font-style: normal;
`;

export const Caracteristicas  = styled.div`
 display:flex;

 >li{
   display:flex;
   text-decoration:none;
   padding:2px;
 }
`;

export const ValorDoCarro  = styled.div``;

export const Moeda  = styled.div``;
